import React from 'react'
import RecipeList from './RecipeList'
import SearchBox from './SearchBox'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      page: 1,
    };
  }
  componentDidMount() {
    fetch(`http://www.recipepuppy.com/api/?i=onions,garlic&q=omelet&p=${this.state.page}`)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.results
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  render() { 
    return (
      <div className="container mx-auto px-4">
        <header>
          <h1 className="text-3xl py-6">Recipe Puppy</h1>
          <p className="text-gray-700 px-6">Enter the recipe you are looking for below</p>
        </header>
        <SearchBox />
        <RecipeList />
      </div>
    );
  }
}

export default App;
