import React from 'react'
import RecipeList from './RecipeList'
import SearchBox from './SearchBox'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      page: 1,
      recipe: 'Omelet',
    };
  }
  fetchItems = (recipe, page) => { 
    fetch(`http://www.recipepuppy.com/api/?q=${this.state.recipe}&p=${this.state.page}`)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.results,
            recipe: recipe
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  turnPage = (increment) => { 
    if (this.state.page + increment > 0) { 
      this.setState({
        page: this.state.page + increment
      })
      this.fetchItems(this.state.recipe, this.state.page)
    }
  }
  searchRecipe = (search) => { 
    this.fetchItems(search, this.state.page)
    this.setState({
        recipe: search
      })
  }
  componentDidMount() {
    this.fetchItems(this.state.recipe, this.state.page)
  }
  render() { 
    return (
      <div className="container mx-auto px-4">
        <header>
          <h1 className="text-3xl py-6">Recipe Puppy</h1>
          <p className="text-gray-700 px-6">Enter the recipe you are looking for below</p>
        </header>
        <SearchBox
          searchRecipe={this.searchRecipe}
        />
        <RecipeList
          items={this.state.items}
          error={this.state.error}
          isLoaded={this.state.isLoaded}
          page={this.state.page}
          turnPage={this.turnPage}
        />
      </div>
    );
  }
}

export default App;
