import React from 'react';

class RecipeList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      page: 1,
    };
  }

  componentDidMount() {
    fetch(`http://www.recipepuppy.com/api/?i=onions,garlic&q=omelet&p=${state.page}`)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.results
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <button onClick={console.log}>Forward</button>
          <button onClick={console.log}>Back</button>
          <ul>
            {items.map(item => (
              <li key={item.title}>
                <a href={item.href}>{item.title}</a>
                {item.thumbnail &&
                  <img src={item.thumbnail} alt={item.title}/>
                }
                <ul>
                {item.ingredients.split(',').map(item => (
                  <li>{item}</li>
                ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      );
    }
  }
}

export default RecipeList
