import React from 'react';

class SearchBox extends React.Component {
  ingredientRef = React.createRef();
  recipeRef = React.createRef();

  render() {
    return (
      <form className="w-full max-w-sm py-4" onSubmit={this.searchRecipes}>
        <div className="flex items-center border-b border-b-2 border-teal-500 py-2">
          <input className="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none" ref={this.recipeRef} type="text" placeholder="Recipe" aria-label="Recipe" />
          <button className="flex-shrink-0 bg-blue-500 hover:bg-blue-700 border-teal-500 hover:border-teal-700 text-sm border-4 text-white py-1 px-2 rounded" type="button">
            Search
          </button>
  </div>
</form>
     )
  }
}

export default SearchBox

