import React from 'react';

class SearchBox extends React.Component {
  ingredientRef = React.createRef();
  recipeRef = React.createRef();

  render() {
    return (
      <form onSubmit={this.searchRecipes}>
        <input name="searchIngredient" ref={this.ingredientRef} type="text" placeholder="Ingredient" />
        <input name="searchRecipe" ref={this.recipeRef} type="text" placeholder="Recipe" />
        <button className="rounded-lg px-4 md:px-5 xl:px-4 py-3 md:py-4 xl:py-3 bg-blue-500 hover:bg-blue-600 md:text-lg xl:text-base text-white font-semibold leading-tight shadow-md" type="submit">Search Recipes</button>
      </form>
     )
  }
}

export default SearchBox
