import React from 'react'
import RecipeList from './RecipeList'
import SearchBox from './SearchBox'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      page: 1,
    };
  }

  fetchItems = (path) => { 
    fetch(`http://www.recipepuppy.com/api/?${path}`)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.results
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  turnPage = (increment) => { 
    this.setState({
      page: this.state.page + increment

    })
  }
  componentDidMount() {
    this.fetchItems(`i=onions,garlic&q=omelet&p=1`)
  }
  render() { 
    return (
      <div className="container mx-auto px-4">
        <header>
          <h1 className="text-3xl py-6">Recipe Puppy</h1>
          <p className="text-gray-700 px-6">Enter the recipe you are looking for below</p>
        </header>
        <SearchBox />
        <RecipeList
          items={this.state.items}
          error={this.state.error}
          isLoaded={this.state.isLoaded}
          page={this.state.page}
        />
      </div>
    );
  }
}

export default App;
